import React, { Component } from "react";
import Alertdialog from './dialogbox';
import Header from "./header";
import Resultfilter from "./result_filter";
import { filterObjectList, rndrSearchResults} from './util';
import Paginator from "./paginator";



class SearchResults extends Component {

  state = {
    dialog:{
      status:false, 
      msg:""
    }
  };



  render() {



    const objList = this.props.objList;

    var filObj = filterObjectList(objList, []);

     //var filObj = filterObjectList(objList, this.state.filterlist);
    var filterInfo = filObj.filterinfo;
    var passedObjList = filObj.passedobjlist;
    var passedCount = passedObjList.length;

    var batchSize = 20;
    var pageCount = parseInt(passedObjList.length/batchSize) + 1;
    pageCount = (objList.length > 0 ? pageCount : 0);

    var startIdx = batchSize * (this.state.pageIdx - 1) + 1;
    var endIdx = startIdx + batchSize;
    endIdx = (endIdx > passedCount ? passedCount : endIdx);

    var filterHideFlag = (objList.length > 0 ? "block" : "none");

    var resultSummary = passedObjList.length + " Results";


   //return (<div>{passedObjList.length}</div>);

    return (
      <div>
      <Header onSearch={this.props.handleSearch} onKeyPress={this.props.handleKeyPress}/>
      <div className="pagecn">
        <Alertdialog dialog={this.state.dialog} onClose={this.props.handleDialogClose}/>
        <div className="leftblock" style={{width:"100%", display:filterHideFlag}}>
          <Resultfilter
            filterinfo={filterInfo}
            resultcount={objList.length}
            resultSummary={resultSummary}
            handleSearchIcon={this.props.handleSearchIcon}
            handleFilterIcon={this.props.handleFilterIcon}
            handleFilterApply={this.props.handleFilterApply}
            handleFilterReset={this.props.handleFilterReset}
          />
        </div>

        <div className="leftblock" 
          style={{width:"100%", margin:"60px 0px 0px 0px",
          borderBottom:"1px solid #ccc"}}>
          <Paginator 
            paginatorId={"top"}
            pageCount={pageCount}
            pageStartIdx={this.props.pageStartIdx}
            pageEndIdx={this.props.pageEndIdx}
            onClick={this.props.handlePaginatorClick}
          />
        </div>

        <div className="leftblock" 
          style={{margin:"20px 0px 0px 0px"}}>
          {rndrSearchResults(passedObjList, startIdx, endIdx)}
        </div>

        <div className="leftblock" 
          style={{width:"100%", 
          margin:"0px 0px 0px 0px",
          padding:"5px 0px 0px 0px",
          borderTop:"1px solid #ccc"}}>
          <Paginator 
            paginatorId={"bottom"}
            pageCount={pageCount}
            pageStartIdx={this.props.pageStartIdx}
            pageEndIdx={this.props.pageEndIdx}
            onClick={this.props.handlePaginatorClick}
          />
        </div>

      </div>

      </div>
    );
  }
}

export default SearchResults;
