import React, { Component } from "react";
import SearchResults from "./components/search_results";
import DatasetPage from "./components/dataset_page";

import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Alertdialog from './components/dialogbox';
//import jsonData from "../jsondata/results.json";
import Header from "./components/header";
import Footer from "./components/footer";
import * as LocalConfig from "./components/local_config";
import Loadingicon from "./components/loading_icon";
import $ from "jquery";


class App extends Component {

  state = {

    dialog:{
      status:false, 
      msg:""
    }
  };





  render() {



    const objList = (this.state.response.recordlist === undefined ? [] : this.state.response.recordlist);

    return (
      <div>
      <Header onSearch={this.handleSearch} onKeyPress={this.handleKeyPress}/>
      <Alertdialog dialog={this.state.dialog} onClose={this.handleDialogClose}/>
      <Router>
        <Switch>
          <Route
            path="/:bcoId"
            render={(props) => (
              <DatasetPage
                bcoId={props.match.params.bcoId}
              />
            )}
          />
          <Route
            exact
            path="/"
            render={(props) => (
              <SearchResults
                objList={objList}
                handleSearchIcon={this.handleSearchIcon}
                handleFilterIcon={this.handleFilterIcon}
                handleFilterApply={this.handleFilterApply}
                handleFilterReset={this.handleFilterReset}
                pageIdx={this.state.pageIdx}
                pageStartIdx={this.state.pageStartIdx}
                pageEndIdx={this.state.pageEndIdx}
                onClick={this.handlePaginatorClick}
              />
            )}
          />
        </Switch>
      </Router>
      <Footer />
      </div>
    );


  }
}

export default App;
