import $ from "jquery";
import React from 'react';
import { Link } from "react-router-dom";




export function verifyReqObj (reqObj, fieldDef){
    var errorList = [];
    for (var f in fieldDef){
      if(!(f in reqObj) === true){
          errorList.push(<li>field "{f}" missing in request</li>);
      }
      else if (reqObj[f].toString() === "" ){
          errorList.push(<li key={"error_in_" + f}>field "{f}" cannot be empty value</li>);
      }
      else if (typeof(reqObj[f]) !== fieldDef[f]["emtype"] ){
        errorList.push(<li>field "{f}" type mismatch</li>);
      }
    }
    if (errorList.length === 0){
        return errorList;
    }
    console.log(errorList);
    return (<ul> {errorList} </ul>);
}

export function getStarList(starCount){

  var starList = [];
  for (var j =1; j <= 5; j++){
    var fg = (j <= starCount ? "#F5B041" : "#cccccc");
    var s = {cursor:"pointer",marginRight:"1px",fontSize:"15px", color:fg};
    starList.push(<i key={"s_"+j} className="material-icons" style={s}>star </i>)
  }
  return starList;
}



export function rndrPrice (priceInfo) {
    var priceParts = priceInfo["price"].toString().split(".");
    if (priceParts[1] === undefined) {
      priceParts[1] = "00";
    }

    return (
      <div>
        <sup>
          <font className="item_price_txt_two">$</font>
        </sup>
        <font className="item_price_txt_one">
          {priceParts[0]}
        </font>
        <sup>
          <font className="item_price_txt_two">
            . {priceParts[1]}
          </font>
        </sup>{" "}
        {priceInfo["currency"]}
      </div>
    );
  }



export function rndrUnitPrice (priceInfo) {
    var priceParts = priceInfo["price"].toString().split(".");
    if (priceParts[1] === undefined) {
      priceParts[1] = "00";
    }

    return (
      <span>${priceParts[0]}.{priceParts[1]} {priceInfo["currency"]}</span>
    );
  }

  export function rndrTotalPrice (priceInfo, itemCount, dateCount) {

    var totalPrice = parseFloat(priceInfo["price"]) * parseFloat(itemCount) * parseFloat(dateCount) ;
    var priceParts = totalPrice.toString().split(".");
    if (priceParts[1] === undefined) {
      priceParts[1] = "00";
    }
    return (
      <span>${priceParts[0]}.{priceParts[1]} {priceInfo["currency"]}</span>
    );
  }






  export function filterObjectList(objList, filterList) {

    var retObj = {filterinfo:{}, passedobjlist:[]};
    for (var i in objList) {
      var obj = objList[i];
      var passCount = 0;
      for (var j in obj.categories) {
        var o = obj.categories[j];
        var combo = o.name + "|" + o.value;
        if (!(o.name in retObj.filterinfo)) {
            //retObj.filterinfo[o.name] = [];
            retObj.filterinfo[o.name] = {};
        }
        //if (retObj.filterinfo[o.name].indexOf(o.value) === -1) {
        if(true){
            //retObj.filterinfo[o.name].push(o.value);
            if (!(o.value in retObj.filterinfo[o.name])){
              retObj.filterinfo[o.name][o.value] = 1;
            }
            else{
              retObj.filterinfo[o.name][o.value] += 1;
            }
        }
        if (filterList.indexOf(combo) !== -1) {
          passCount += 1;
        }
      }
      //if (passCount === this.state.filterlist.length){
      if (filterList.length > 0) {
        if (passCount > 0) {
            retObj.passedobjlist.push(obj);
        }
      } else {
        retObj.passedobjlist.push(obj);
      }
    }

    return retObj;
}



export function shortText(txt, txtLen) {
  var shortText = "";
  var parts = txt.split(" ");
  for (var j in parts) {
    if (shortText.length < txtLen) {
      shortText += parts[j] + " ";
    } else {
      shortText += " ...";
      break;
    }
  }
  return shortText;
}



export function rndrSearchResults(objList) {

  const d1 = { width: "100%", margin: "0px 0px 20px 0px" };
  const d2 = { fontSize: "13px" };
  const d3 = { color: "#999" };

  return  (
    <div>
    {objList.map((obj) => (
      <div key={obj.name} style={d1}>
        <div style={d2} key={objList.indexOf(obj)}>
          {obj.url} {">"} {obj.categories[0].value}{" "}
        </div>
        <div key="id_12">
          <Link className="reglink" 
            to={
                { pathname: obj.url, 
                  state: { 
                    siteinfo:{
                      siteid:obj.siteid, sitekey:obj.sitekey,
                      bsnsid:obj.bsnsid,
                    name:obj.name,reviews:obj.reviews}
                  }
                }
              }
          >{obj.name} - {obj.motto}</Link>
        </div>
        <div key="id_13">{shortText(obj.description, 200)}</div>
        <div style={d3}>{obj.city} {","} {obj.country}</div>
      </div>
    ))}
    </div>);

}
