import React, { Component } from "react";
import Alertdialog from './dialogbox';
import Globalheader from "./global_header";
import Globalfooter from "./global_footer";
import Resultfilter from "./result_filter";

import $ from "jquery";
import { filterObjectList, rndrSearchResults} from './util';
import * as Zulamodule from "./module_exports";
import Loadingicon from "./loading_icon";
import Searchbox from "./search_box";


class Globalcn extends Component {
  state = {
    filterlist: [],
    dialog:{
      status:false, 
      msg:""
    }
  };

  componentDidMount() {
    this.handleSearch();
  }

  handleKeyPress = (e) => {
    if(e.key === "Enter"){
      e.preventDefault();
      this.handleSearch();
    }
  }

  handleSearch = () => {

    var queryValue = $("#query").val();
    queryValue = (queryValue === null ? "" : queryValue);
    var reqObj = {coll:"c_bsns", query:queryValue};

    const requestOptions = { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(reqObj)
    };
    const svcUrl = Zulamodule.apiHash.get_many;


    fetch(svcUrl, requestOptions)
      .then((res) => res.json())
      .then(
        (result) => {
          var tmpState = this.state;
          tmpState.response = result;
          tmpState.isLoaded = true;          
          if (tmpState.response.status === 0){
            tmpState.dialog.status = true;
            tmpState.dialog.msg = tmpState.response.error;
          }
          this.setState(tmpState);
          //console.log("Request:",svcUrl);
          console.log("Ajax response:", result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }


  handleFilterReset = () => {
    $('input[name="filtervalue"]:checkbox:checked').prop("checked", false);
    this.setState({ filterlist: [] });
  };

  handleFilterApply = () => {
    $("#filtercn").toggle();
    var tmpList = $('input[name="filtervalue"]:checkbox:checked')
      .map(function () {
        return $(this).val();
      })
      .get(); // <----
    this.setState({ filterlist: tmpList });
  };

  handleFilterIcon = () => {
    $("#filtercn").toggle();
  };



  handleDialogClose = () => {
    var tmpState = this.state;
    tmpState.dialog.status = false;
    this.setState(tmpState);
  }





  render() {


    if (!("response" in this.state)){
      return <Loadingicon/>
    }

    //const objList = (this.props.objlist === undefined ? [] : [...this.props.objlist]);
    const objList = (this.state.response.recordlist === undefined ? [] : this.state.response.recordlist);

    var filObj = filterObjectList(objList, this.state.filterlist);
    var filterInfo = filObj.filterinfo;
    var passedObjList = filObj.passedobjlist;



    var resultSummary = passedObjList.length + " Results";


    return (
      <div>
      <Globalheader />
      <div className="pagecn">
        <Alertdialog dialog={this.state.dialog} onClose={this.handleDialogClose}/>

        <div className="searchbox_div_one">
            <div className="searchbox_div_two">
              <Searchbox 
                handleKeyPress={this.handleKeyPress}
                handleSearch={this.handleSearch}
                placeholder="search zulasites"
              />
            </div>
        </div>

        <Resultfilter
          filterinfo={filterInfo}
          resultcount={objList.length}
          resultSummary={resultSummary}
          handleSearchIcon={this.handleSearchIcon}
          handleFilterIcon={this.handleFilterIcon}
          handleFilterApply={this.handleFilterApply}
          handleFilterReset={this.handleFilterReset}
        />

        <div className="leftblock" style={{margin:"20px 0px 0px 0px"}}>
          {rndrSearchResults(passedObjList)}
        </div>

      </div>
      <Globalfooter />
      </div>
    );
  }
}

export default Globalcn;
