import React, { Component } from "react";
import Alertdialog from './dialogbox';
import Loadingicon from "./loading_icon";
import * as LocalConfig from "./local_config";
import { Chart } from "react-google-charts";
import { Link } from "react-router-dom";



class DatasetPage extends Component {

  state = {
    dialog:{
      status:false, 
      msg:""
    }
  };


  handleDialogClose = () => {
    var tmpState = this.state;
    tmpState.dialog.status = false;
    this.setState(tmpState);
  }

  componentDidMount() {
    var reqObj = {coll:"c_bco", bcoid:this.props.bcoId};
    const requestOptions = { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(reqObj)
    };
    const svcUrl = LocalConfig.apiHash.get_one;


    fetch(svcUrl, requestOptions)
      .then((res) => res.json())
      .then(
        (result) => {
          var tmpState = this.state;
          tmpState.response = result;
          tmpState.isLoaded = true;          
          if (tmpState.response.status === 0){
            tmpState.dialog.status = true;
            tmpState.dialog.msg = tmpState.response.error;
          }
          this.setState(tmpState);
          //console.log("Request:",svcUrl);
          console.log("Ajax response:", result);
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }

  render() {

    if (!("response" in this.state)){
      return <Loadingicon/>
    }

    const bcoObj = (this.state.response.record !== undefined ? this.state.response.record : {});

    bcoObj.sampleData = [
      [
        { type: 'string', label: 'A' },
        { type: 'string', label: 'B' },
        { type: 'string', label: 'C' },
      ],
      [
        "Mike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa faMike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa fa", 
        "ffaaMike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa faMike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa fa", 
        "dfafaMike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa faMike fdsf ffd ff fa fafasfa fa ffds fdsafafda fdafsdfa fafdafa fa"
      ]
    ];


    var tabTitleList = [];
    var activeFlag = "active";

    var btnStyle = {width:"100%", color:"#333", border:"1px solid #ccc"};
    btnStyle.color = (activeFlag === "active" ? "#990000" : "#333");
    btnStyle.background = (activeFlag === "active" ? "#fff" : "#eee");
    btnStyle.borderBottom = (activeFlag === "active" ? "1px solid #fff" : "1px solid #ccc");


    var tabTitleList= [
        (<li key={"tab-sample_view"} className="nav-item" role="presentation" 
            style={{width:"33%"}}>
            <button className={"nav-link " + activeFlag} id={"sample_view-tab"}  data-bs-toggle="tab" 
            data-bs-target={"#sample_view"} type="button" role="tab" aria-controls={"sample_view-cn"} aria-selected="true"
            style={btnStyle} onClick={this.handleTitleClick}
            >
             Sample View  
            </button>
        </li>),
        (<li key={"tab-json_view"} className="nav-item" role="presentation" 
        style={{width:"33%"}}>
        <button className={"nav-link " + activeFlag} id={"json_view-tab"}  data-bs-toggle="tab" 
        data-bs-target={"#json_view"} type="button" role="tab" aria-controls={"json_view-cn"} aria-selected="true"
        style={btnStyle} onClick={this.handleTitleClick}
        >
         JSON View  
        </button>
        </li>),
    (<li key={"tab-readme_view"} className="nav-item" role="presentation" 
    style={{width:"33%"}}>
    <button className={"nav-link " + activeFlag} id={"readme_view-tab"}  
    data-bs-toggle="tab" data-bs-target={"#readme_view"} type="button" role="tab" aria-controls={"readme_view-cn"} aria-selected="true"
    style={btnStyle} onClick={this.handleTitleClick}
    >
     README   
    </button>
    </li>),
    ];

    var tabContentList = [
      (<div key={"formcn-sample_view"} className={"tab-pane fade show  " + activeFlag} 
        id={"sample_view-cn"} role="tabpanel" aria-labelledby={"sample_view-tab"}>
          sample_view
      </div>),
      (<div key={"formcn-json_view"} className={"tab-pane fade show  " + activeFlag} 
      id={"sample_view-cn"} role="tabpanel" aria-labelledby={"json_view-tab"}>
        json_view
      </div>),
      (<div key={"formcn-readme_view"} className={"tab-pane fade show  " + activeFlag} 
        id={"readme_view-cn"} role="tabpanel" aria-labelledby={"readme_view-tab"}>
        readme_view
      </div>)
    ];




    return (
      <div className="pagecn">
        <Alertdialog dialog={this.state.dialog} onClose={this.handleDialogClose}/>

        <div className="leftblock" style={{width:"100%", margin:"100px 0px 0px 0px"}}>
          <span>{bcoObj.bcoid} sample view</span><br/>
          <span style={{fontWeight:"bold"}}>{bcoObj.title}</span><br/>
          <span>{bcoObj.description}</span><br/>
        </div>

        <div className="leftblock" 
          style={{width:"100%", margin:"20px 0px 0px 0px", 
            textAlign:"right", borderBottom:"1px solid #ccc"}}>
          <ul className="nav nav-tabs" id="myTab" role="tablist">
            {tabTitleList}
          </ul>
          <div className="tab-content" id="myTabContent" 
            style={{margin:"40px 0px 0px 0px"}}>
            {tabContentList}
          </div>
        </div>





        <div className="leftblock" 
          style={{width:"100%", minHeight:"100px", 
            margin:"5px 0px 0px 0px", 
            padding:"10px", border:"1px solid #ccc"}}>
          <div className="leftblock" 
          style={{width:"100%",  overflow:"auto", }}>
            <Chart
              width={'100%'}
              chartType="Table"
              loader={<div>Loading Chart</div>}
              data={bcoObj.sampleData}
              options={{
                showRowNumber: false,
              }}
              rootProps={{ 'data-testid': '1' }}
            />
        </div>
        </div>
      </div>
    );
  }
}

export default DatasetPage;
