import React, { Component } from "react";
import SearchResults from "./components/search_results";
import DatasetPage from "./components/dataset_page";

import "./App.css";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Alertdialog from './components/dialogbox';
//import jsonData from "../jsondata/results.json";
import Header from "./header";
import Footer from "./footer";
import * as LocalConfig from "./local_config";
import Loadingicon from "./loading_icon";
import $ from "jquery";


class App extends Component {


  state = {
    filterlist: [],
    pageIdx:1,
    pageBatchSize:5,
    pageStartIdx:1,
    pageEndIdx:5,
    dialog:{
      status:false, 
      msg:""
    }
  };

  componentDidMount() {
    this.handleSearch();
  }

  handleKeyPress = (e) => {
    if(e.key === "Enter"){
      e.preventDefault();
      this.handleSearch();
    }
  }



   handleSearch = () => {
    var queryValue = $("#query").val();
    queryValue = (queryValue === undefined ? "" : queryValue);
    var reqObj = {coll:"c_bco", query:queryValue};


    const requestOptions = { 
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(reqObj)
    };
    const svcUrl = LocalConfig.apiHash.get_many;

    fetch(svcUrl, requestOptions)
      .then((res) => res.json())
      .then(
        (result) => {
          var tmpState = this.state;
          tmpState.response = result;
          tmpState.isLoaded = true;          
          if (tmpState.response.status === 0){
            tmpState.dialog.status = true;
            tmpState.dialog.msg = tmpState.response.error;
          }
          this.setState(tmpState);
          //console.log("Request:",svcUrl);
          console.log("Ajax response:", result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }


  handleFilterReset = () => {
    $('input[name="filtervalue"]:checkbox:checked').prop("checked", false);
    this.setState({ filterlist: [] });
  };

  handleFilterApply = () => {
    $("#filtercn").toggle();
    var tmpList = $('input[name="filtervalue"]:checkbox:checked')
      .map(function () {
        return $(this).val();
      })
      .get(); // <----
    this.setState({ filterlist: tmpList });
  };

  handleFilterIcon = () => {
    $("#filtercn").toggle();
  };



  handleDialogClose = () => {
    var tmpState = this.state;
    tmpState.dialog.status = false;
    this.setState(tmpState);
  }


  handlePaginatorClick = (e) => {
    if(e.target.id.indexOf("_disabled") !== -1){
      return;
    }
    var pageIdx = parseInt(e.target.id.split("_")[2]);
    var tmpState = this.state;
    if(e.target.id.indexOf("_next_") !== -1){
      tmpState.pageStartIdx = tmpState.pageEndIdx + 1;
      tmpState.pageEndIdx += tmpState.pageBatchSize;
      pageIdx = tmpState.pageStartIdx;
    }
    else if(e.target.id.indexOf("_prev_") !== -1){
      tmpState.pageStartIdx -= tmpState.pageBatchSize;
      tmpState.pageEndIdx = tmpState.pageStartIdx + tmpState.pageBatchSize - 1;
      pageIdx = tmpState.pageStartIdx;
    }
    tmpState.pageIdx = pageIdx;
    this.setState(tmpState);

  }



  render() {

    if (!("response" in this.state)){
      return <Loadingicon/>
    }

    const objList = (this.state.response.recordlist === undefined ? [] : this.state.response.recordlist);

    return (
      <div>
      <Header onSearch={this.handleSearch} onKeyPress={this.handleKeyPress}/>
      <Alertdialog dialog={this.state.dialog} onClose={this.handleDialogClose}/>
      <Router>
        <Switch>
          <Route
            path="/:bcoId"
            render={(props) => (
              <DatasetPage
                bcoId={props.match.params.bcoId}
              />
            )}
          />
          <Route
            exact
            path="/"
            render={(props) => (
              <SearchResults
                objList={objList}
              />
            )}
          />
        </Switch>
      </Router>
      <Footer />
      </div>
    );


  }
}

export default App;
